package admission

import (
	"sync"

	api "kubedb.dev/apimachinery/apis/kubedb/v1alpha2"
	cs "kubedb.dev/apimachinery/client/clientset/versioned"

	"gomodules.xyz/pointer"
	admission "k8s.io/api/admission/v1"
	"k8s.io/apimachinery/pkg/runtime"
	"k8s.io/apimachinery/pkg/runtime/schema"
	"k8s.io/client-go/kubernetes"
	"k8s.io/client-go/rest"
	meta_util "kmodules.xyz/client-go/meta"
	hookapi "kmodules.xyz/webhook-runtime/admission/v1"
	"kmodules.xyz/webhook-runtime/builder"
)

const (
	defaultPoolMode = "session"
)

type PgBouncerMutator struct {
	client      kubernetes.Interface
	extClient   cs.Interface
	lock        sync.RWMutex
	initialized bool
}

var _ hookapi.AdmissionHook = &PgBouncerMutator{}

func (a *PgBouncerMutator) Resource() (plural schema.GroupVersionResource, singular string) {
	return builder.MutatorResource(api.Kind(api.ResourceKindPgBouncer))
}

func (a *PgBouncerMutator) Initialize(config *rest.Config, stopCh <-chan struct{}) error {
	a.lock.Lock()
	defer a.lock.Unlock()

	a.initialized = true

	var err error
	if a.client, err = kubernetes.NewForConfig(config); err != nil {
		return err
	}
	if a.extClient, err = cs.NewForConfig(config); err != nil {
		return err
	}
	return err
}

func (a *PgBouncerMutator) Admit(req *admission.AdmissionRequest) *admission.AdmissionResponse {
	status := &admission.AdmissionResponse{}

	// N.B.: No Mutating for delete
	if (req.Operation != admission.Create && req.Operation != admission.Update) ||
		len(req.SubResource) != 0 ||
		req.Kind.Group != api.SchemeGroupVersion.Group ||
		req.Kind.Kind != api.ResourceKindPgBouncer {
		status.Allowed = true
		return status
	}

	a.lock.RLock()
	defer a.lock.RUnlock()
	if !a.initialized {
		return hookapi.StatusUninitialized()
	}
	obj, err := meta_util.UnmarshalFromJSON(req.Object.Raw, api.SchemeGroupVersion)
	if err != nil {
		return hookapi.StatusBadRequest(err)
	}

	pbMod := setDefaultValues(obj.(*api.PgBouncer).DeepCopy())
	patch, err := meta_util.CreateJSONPatch(req.Object.Raw, pbMod)
	if err != nil {
		return hookapi.StatusInternalServerError(err)
	}
	status.Patch = patch
	patchType := admission.PatchTypeJSONPatch
	status.PatchType = &patchType

	status.Allowed = true
	return status
}

// setDefaultValues provides the defaulting that is performed in mutating stage of creating/updating a PgBouncer database
func setDefaultValues(db *api.PgBouncer) runtime.Object {
	if db.Spec.Replicas == nil {
		db.Spec.Replicas = pointer.Int32P(1)
	}

	// TODO: Make sure an image path is set

	if db.Spec.ConnectionPool != nil {
		if db.Spec.ConnectionPool.Port == nil {
			db.Spec.ConnectionPool.Port = pointer.Int32P(api.PgBouncerDatabasePort)
		}
		if db.Spec.ConnectionPool.PoolMode == "" {
			db.Spec.ConnectionPool.PoolMode = defaultPoolMode
		}
	}
	db.SetDefaults()

	return db
}
